INSERT INTO `movies` (`id`, `title`, `image`, `aparat_link`, `content`, `tags`, `created_at`, `updated_at`) VALUES
(1, 'ایران زیبا - زیباترین نقاط گردشگری ایران', '823859.png', 'gZ4Nz', NULL, 'گردشگری/جاذبه های گردشگری', '2021-12-16 10:59:13', '2021-12-16 11:00:28'),
(2, 'موزه ملی ایران', '794879.png', '2tAWq', '<p dir=\"rtl\">موزهٔ ملی ایران مجموعه&zwnj;ای از گنجینهٔ آثار تاریخی و پیش از تاریخی ایران در شهر تهران قرار دارد. این موزه شامل دو ساختمان مجزا به نام&zwnj;های موزهٔ ایران باستان با تاریخ گشایش ۱۳۱۶ و موزهٔ باستان&zwnj;شناسی و هنر دوران اسلامی ایران با تاریخ گشایش ۱۳۷۵ است. موزهٔ ملی ایران مهم&zwnj;ترین موزهٔ کشور در خصوص نگهداری، نمایش و پژوهش مجموعه&zwnj;های باستان&zwnj;شناختی ایران است. گستردگی و تنوع مجموعهٔ اشیا موجود در موزه به گستردگی جغرافیایی کشور و دوران&zwnj;های متعدد پیش از تاریخی، تاریخی و اسلامی است که انسان در این سرزمین زیسته و آثاری از خود به جای نهاده&zwnj;است. در حال حاضر، موزه ملی به نمایش آثاری پرداخته که بیان&zwnj;گر غنای فرهنگ و تمدن، هنر، رشد اقتصادی و دست&zwnj;آوردهای فناوری ایرانیان است. موزه ملی ایران با قدمتی حدود هشتاد سال، نه تنها بزرگترین موزهٔ باستان&zwnj;شناسی و تاریخ ایران است، بلکه در فرهنگ موزه&zwnj;داری ایران به عنوان موزهٔ مادر محسوب می&zwnj;شود.</p>', 'موزه ملی/موزه/باستان شناسی', '2021-12-16 11:01:27', '2021-12-16 11:03:07'),
(3, 'مکان های دیدنی اصفهان', '650455.png', 'w0y8A', NULL, 'اصفهان/ مکان های دیدنی اصفهان', '2021-12-16 11:07:36', '2021-12-16 11:08:15'),
(4, 'دیدنی های شیراز حافظ شیرازی', '638542.png', 'foipl', NULL, 'گردشگری/ دیدنی های شیراز/ مکان های دیدنی', '2021-12-16 11:09:27', '2021-12-16 11:10:27'),
(5, 'دیدنی های مازندران', '948406.png', 'hzun0', '<div class=\"single-details__description\">\n<p class=\"sc-kEjbQP ejQjaz paragraph\" dir=\"rtl\">مازندران بلندترین (کوه دماوند) و پست ترین (دریای مازندران) نقاط ایران را در خود جای داده است. این استان یکی از پرجمعیت ترین مناطق از لحاظ تراکم جمعیتی و یکی از غنی ترین آنها از لحاظ منابع گوناگون زیرزمینی می باشد. این استان دارای ۲۲ شهرستان است. مازندران قدیمیترین مردم را در جهان از نظر یک جا نشینی دارد.</p>\n</div>', 'دیدنی های مازندران/ مازندران', '2021-12-16 11:12:27', '2021-12-16 11:15:30');

